
public class IteracionElementos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String frutas [] = {"mango","pera","manzana","uva","mamey"};
		
		for(String fruta:frutas ) {	
			System.out.println(fruta); 
		}
		System.out.println("-------------------");
		System.out.print(frutas[1]);
	}
}
